<?php	session_start();	if(!isset($_SESSION['user']) || $_SESSION['user']['acceso']!='cpanel'):		header("Location:../");		exit();	endif;	include("../conexion.php");	$query = "SELECT idCat id, titulo FROM ctlg_categorias WHERE tipo='tag'";	$result = mysql_query($query);	if($result):		$autocompletiondata = array();		while($row = mysql_fetch_array($result)):			$autocompletiondata[$row['id']] = $row['titulo'];		endwhile;		if(!empty($_GET['term'])) {			$response = array();			foreach($autocompletiondata as $key => $value) {				if(strlen($_GET['term']) == 0 || strpos(strtolower($value), strtolower($_GET['term'])) !== false) {					$response[] = '{"id":'.$key.',"label":"'.$value.'","value":"'.$value.'"}';				}			}					echo "[".implode(',', $response)."]";		}	endif;	?>