<?php	session_start();	if(!isset($_SESSION['user']) || $_SESSION['user']['acceso']!='cpanel'):		header("Location:../");		exit();	endif;	require("../conexion.php");	require("../scripts/funciones.php");	require("../ubica_carpetas.php");	require("../scripts/gump.class.php");
	$gump = new GUMP();
	$_GET = $gump->sanitize($_GET);	$url_ref = $_SERVER['HTTP_REFERER'];	$carpetaUpload = "../".$carpeta_imagenes;	$carpetaThumbs = $carpetaUpload."thumbs/";		$idUser = $_SESSION['user']['idUser'];	$titulo_esp = $_POST['titulo_esp'];	$resumen_esp = $_POST['resumen_esp'];	$contenido_esp = $_POST['contenido_esp'];	$titulo_eng = $_POST['titulo_eng'];	$resumen_eng = $_POST['resumen_eng'];	$contenido_eng = $_POST['contenido_eng'];	$tipo = $_POST['tipo'];	$fechaPublicacion = $_POST['aa'] . "-" . $_POST['mes'] . "-" . $_POST['dd']." 00:00:00";	$fechaFinal = $_POST['aaE'] . "-" . $_POST['mesE'] . "-" . $_POST['ddE']." 00:00:00";	$slug = $_POST['preSlug'].quitarAcentos($titulo_esp);	$opciones = @serialize($_POST['opciones']);	$orden = $_POST['orden'];	$estatus = $_POST['estatus'];	$mostrarEn  = $_POST['mostrarEn'];	$keywords  = $_POST['keywords'];	$idCat = $_POST['idCat'];
	/*insert*/	if(!empty($_POST["add"]) && postBlock($_POST['postID'])):		if(!empty($titulo_esp)):
			$queryAdd = "INSERT INTO ctlg_entradas(idUser, titulo_esp, contenido_esp, resumen_esp, titulo_eng, contenido_eng, resumen_eng, slug, tipo, opciones, orden, fechaPublicacion, fechaModificacion, fechaFinal, estatus, mostrarEn, keywords) VALUES('{$idUser}', '{$titulo_esp}', '{$contenido_esp}', '{$resumen_esp}', '{$titulo_eng}', '{$contenido_eng}', '{$resumen_eng}', '{$slug}', '{$tipo}', '{$opciones}', '{$orden}', '{$fechaPublicacion}', NOW(), '{$fechaFinal}', '{$estatus}', '{$mostrarEn}', '{$keywords}')";			$resultAdd = mysql_query($queryAdd);			$nid = mysql_insert_id();			if($resultAdd):				$mensaje = 'La informaci&oacute;n se guardo con exito';				$clase = 'exito';				//guarda tags/categorias				updateCatsTags($nid, $idCat);								$config = array(					"carpetaUpload" => $carpetaUpload,					"carpetaThumbs" => $carpetaThumbs				);				procesaImagenes($nid, $config);							else:				$error = mysql_error();				$mensaje = "La informaci&oacute;n no se pudo guardar $error";				$clase = 'error';			endif;		else:			$mensaje = "Uno o m&aacute;s campos parecen estar vacios";			$clase = "alerta";			endif;		escribeBitacora($idUser, "[ins] tabla: ctlg_entradas, id: {$nid}, titulo: {$titulo}, tipo: {$tipo}");		$_SESSION['resultado'] = array("mensaje"=>$mensaje, "clase"=>$clase);	endif;	/*edit*/	if(!empty($_POST["edit"]) && postBlock($_POST['postID'])):		if(!empty($titulo_esp)):			$nid = $_POST['id'];
			$queryEdit = "UPDATE ctlg_entradas SET titulo_esp='{$titulo_esp}', contenido_esp='{$contenido_esp}', resumen_esp='{$resumen_esp}', titulo_eng='{$titulo_eng}', contenido_eng='{$contenido_eng}', resumen_eng='{$resumen_eng}', slug='{$slug}', tipo='{$tipo}', opciones='{$opciones}', orden='{$orden}', fechaPublicacion='{$fechaPublicacion}', fechaModificacion=NOW(), fechaFinal='{$fechaFinal}', estatus='{$estatus}', mostrarEn='{$mostrarEn}', keywords='{$keywords}' WHERE idEntrada='{$nid}'";			$resultEdit = mysql_query($queryEdit);			if($resultEdit):				$mensaje = 'La informaci&oacute;n se actualizo con exito';				$clase = 'exito';				//guarda tags/categorias				updateCatsTags($nid, $idCat);								$config = array(					"carpetaUpload" => $carpetaUpload,					"carpetaThumbs" => $carpetaThumbs				);				procesaImagenes($nid, $config);
				$nameInput = 'nomArchivoGaleria';				actualizaImagenes($nameInput);			else:				$error = mysql_error();				$mensaje = "La informaci&oacute;n no se pudo actualizar. $error";				$clase = 'error';			endif;		else:			$mensaje = "Uno o m&aacute;s campos parecen estar vacios";			$clase = "alerta";		endif;				$url_ref = $_POST['url_ref'];		escribeBitacora($idUser, "[edit] tabla: ctlg_entradas, id: {$nid}, titulo: {$titulo}, tipo: {$tipo}");		$_SESSION['resultado'] = array("mensaje"=>$mensaje, "clase"=>$clase);	endif;	/*delete*/	if(isset($_GET["haccion"]) && $_GET["haccion"]=="eliminar"):		$nid = mysql_real_escape_string($_GET['hid']);
		$queryDel = "DELETE FROM ctlg_entradas WHERE idEntrada='{$nid}'";		$resultDel = mysql_query($queryDel);		if($resultDel):			$mensaje = 'La informaci&oacute;n se elimino con exito';			$clase = 'exito';
		else:			$error = mysql_error();			$mensaje = "La informaci&oacute;n no se pudo eliminar. $error";			$clase = 'error';		endif;		escribeBitacora($idUser, "[del] tabla: ctlg_entradas, id: {$nid}");		$_SESSION['resultado'] = array("mensaje"=>$mensaje, "clase"=>$clase);	endif;	/*volvemos a la pagina*/	header("location:".$url_ref);?>