<?php	session_start();
	if(!isset($_SESSION['user']) || $_SESSION['user']['acceso']!='cpanel'):		header("Location:../");		exit();	endif;		require("../scripts/gump.class.php");	$gump = new GUMP();	$_GET = $gump->sanitize($_GET);		/*delete*/	if(!empty($_GET["haccion"])):		require("../conexion.php");		require("../scripts/funciones.php");		require("../ubica_carpetas.php");		$url_ref = $_SERVER['HTTP_REFERER'];
		$hid = $_GET['hid'];		$tipo = $_GET['tipo'];		$metodo = $_GET['metodo'];		$queryFile = "SELECT nomArchivo FROM ctlg_imagenes WHERE idImagen='{$hid}' LIMIT 0,1";		$resultFile = mysql_query($queryFile);
		if($resultFile && mysql_num_rows($resultFile)):			$file = mysql_fetch_array($resultFile);			$queryDel = "DELETE FROM ctlg_imagenes WHERE idImagen='{$hid}'";			$resultDel = mysql_query($queryDel);			if($resultDel):				if($tipo == "image"):					$nomFile = $hid."_image_".$file['nomArchivo'];					@unlink("../".$carpeta_imagenes.$nomFile);					@unlink("../".$carpeta_imagenes.'thumbs/'.$nomFile);				elseif($tipo == "file"):					@unlink("../".$carpeta_archivos.$hid."_".$file['nomArchivo']);				endif;				$mensaje = "La informaci&oacute;n se elimino con exito";				$clase = "exito";			else:				$mensaje = "La informaci&oacute;n no se pudo actualizar";				$clase = "alerta";			endif;		else:			$mensaje = "La informaci&oacute;n no se pudo eliminar";			$clase = "error";		endif;				$resultado = array("resultado"=>"true", "mensaje"=>$mensaje, "clase"=>$clase);		/*volvemos a la pagina*/		if($metodo == "ajax"):			echo json_encode($resultado);			unset($_SESSION['resultado']);		else:			$_SESSION['resultado'] = $resultado;			header("location:".$url_ref);		endif;	endif;?>