<?php	session_start();	if(!isset($_SESSION['user']) || $_SESSION['user']['acceso']!='cpanel'):		header("Location:../");		exit();	endif;	include("../conexion.php");	include("../scripts/funciones.php");	include("../ubica_carpetas.php");	$url_ref = $_SERVER['HTTP_REFERER'];		$carpetaUpload = "../".$carpeta_imagenes;	$carpetaThumbs = $carpetaUpload."thumbs/";
	$setupUpload = array(		"nameInput" => 'imageSlide',		"tipoRegistro" => 'imageSlide',		"carpetaUpload" => $carpetaUpload,		"carpetas" => array(			array(				"carpetaOrigen" => $carpetaUpload,				"carpetaDestino" => $carpetaUpload,				"ancho" => 1280,				"alto" => 0			),			array(				"carpetaOrigen" => $carpetaUpload,				"carpetaDestino" => $carpetaThumbs,				"ancho" => 310,				"alto" => 0			)		)	);		$titulo = $_POST['titulo'];	$estatus = $_POST['estatus'];	$fecha = $_POST['aa']."-".$_POST['mes']."-".$_POST['dd']." 00:00:00";	$opciones = serialize($_POST['opciones']);	$tipo = $_POST['tipo'];	/*insert*/	if(!empty($_POST["add"]) && postBlock($_POST['postID'])):		if(!empty($titulo)):			
			$query_add = "INSERT INTO ctlg_sliders(titulo, estatus, fechaPublicacion, opciones, tipo) VALUES('{$titulo}', '{$estatus}', '{$fecha}', '{$opciones}', '{$tipo}')";			$result_add = mysql_query($query_add);			$nid = mysql_insert_id();
			if($result_add):				$mensaje = 'La informaci&oacute;n se guardo con exito';				$clase = 'exito';				$setupUpload['nid'] = $nid;				guardaImagenes($setupUpload);			else:				$error = mysql_error();				$mensaje = "La informaci&oacute;n no se pudo guardar $error";				$clase = 'error';			endif;		else:			$mensaje = "Uno o m&aacute;s campos parecen estar vacios";			$clase = "alerta";		endif;		$_SESSION['resultado'] = array("mensaje"=>$mensaje, "clase"=>$clase);	endif;
	/*edit*/	if(!empty($_POST["edit"]) && postBlock($_POST['postID'])):		if(!empty($titulo)):			$nid = $_POST['id'];
			$query_edit = "UPDATE ctlg_sliders SET titulo='{$titulo}', estatus='{$estatus}', fechaPublicacion='{$fecha}', opciones='{$opciones}' WHERE idSlider='{$nid}'";			$result_edit = mysql_query($query_edit);			if($result_edit):				$mensaje = 'La informaci&oacute;n se actualizo con exito,';				$clase = 'exito';				$setupUpload['nid'] = $nid;				guardaImagenes($setupUpload);				$nameInput = 'imageSlide';				actualizaImagenes($nameInput);			else:				$error = mysql_error();				$mensaje = "La informaci&oacute;n no se pudo actualizar. $error";				$clase = 'error';			endif;		else:			$mensaje = "Uno o m&aacute;s campos parecen estar vacios";			$clase = "alerta";		endif;		$_SESSION['resultado'] = array("mensaje"=>$mensaje, "clase"=>$clase);		$url_ref = $_POST['url_ref'];	endif;
	/*delete*/	if(isset($_GET["haccion"]) && $_GET["haccion"]=="eliminar"):		$nid = mysql_real_escape_string($_GET['hid']);		$query_del = "DELETE FROM ctlg_sliders WHERE idSlider='{$nid}'";		$result_del = mysql_query($query_del);		if($result_del):			$mensaje = 'La informaci&oacute;n se elimino con exito';			$clase = 'exito';		else:			$error = mysql_error();			$mensaje = "La informaci&oacute;n no se pudo eliminar. $error";			$clase = 'error';		endif;		$_SESSION['resultado'] = array("mensaje"=>$mensaje, "clase"=>$clase);	endif;
	/*volvemos a la pagina*/	header("location:".$url_ref);?>