<div class="seccion">	<p><label><strong>Bloque Lateral</strong></label></p>	<select name="opciones[bloque-aside]">		<option value="">Ninguno</option>		<?php			$query = "SELECT idEntrada id, titulo_esp titulo FROM ctlg_entradas WHERE tipo='aside-block'";			$result = mysql_query($query);			if($result):				while($item = mysql_fetch_array($result)):					$selected = ($item['id'] == $opciones['bloque-aside'])?"selected":"";?>					<option value="<?php echo $item['id']?>" <?php echo $selected?>><?php echo $item['titulo']?></option>			<?php				endwhile;			endif;?>	</select></div>