<div class="info-box">	<h3>Bloques Custom</h3>	<?php		if(count($opciones['bloque-custom'])):			$query = "SELECT idEntrada id, titulo_esp titulo FROM ctlg_entradas WHERE tipo='static-block'";			$result = mysql_query($query);						if($result):				while($item = mysql_fetch_array($result)):					$blocks[] = $item;				endwhile;			endif;					foreach($opciones['bloque-custom'] as $idBlock):				if($idBlock!=""):?>
					<div class="seccion">						<p>Bloque Custom<br>							<select name="opciones[bloque-custom][]">								<option value="">Ninguno</option>								<?php									foreach($blocks as $block):										$selected = ($block['id'] == $idBlock)?"selected":"";?>										<option value="<?php echo $block['id']?>" <?php echo $selected?>><?php echo $block['titulo']?></option>								<?php									endforeach;?>							</select>						</p>					</div>			<?php				endif;			endforeach;		endif;?>	<div id="cont-bloque-custom" class="seccion">		<?php			$query = "SELECT idEntrada id, titulo_esp titulo FROM ctlg_entradas WHERE tipo='static-block'";			$result = mysql_query($query);
			if($result):?>				<p>Bloque Custom</p>				<select name="opciones[bloque-custom][]">					<option value="">Ninguno</option>					<?php						while($item = mysql_fetch_array($result)):?>							<option value="<?php echo $item['id']?>"><?php echo $item['titulo']?></option>					<?php						endwhile;?>				</select>		<?php			endif;?>	</div>		<div id="clones-bloques-custom"></div>	<div class="cont-btn-pub">		<p><input class="btn_submit clone-element" type="button" data-clone="#cont-bloque-custom" data-cloneTo="#clones-bloques-custom" value="Agragar Otro Bloque" /></p>	</div><!--.cont-btn-pub--></div><!--.info-box-->