<div class="seccion">	<p><label><strong><?php echo $tituloSlideshow?></strong></label></p>	<select name="opciones[configuracion][slideshow-<?php echo $idiomaSlideshow?>]">		<option value="0">Ninguno</option>		<?php			$query = "SELECT idSlider id, titulo FROM ctlg_sliders";			$result = mysql_query($query);			if($result and mysql_num_rows($result)):				while($item = mysql_fetch_array($result)):					$selected = ($opciones['configuracion']['slideshow-'.$idiomaSlideshow] == $item['id'])?"selected":"";?>					<option value="<?php echo $item['id']?>" <?php echo $selected;?>><?php echo $item['titulo']?></option>		<?php				endwhile;			endif;?>	</select></div><!--.seccion-->